sp_widget.script:
(function () {

	data.items = [];

	var grTile = new GlideRecord('u_gdep_tile');
	grTile.addQuery('u_widget_instance', $sp.getInstanceRecord().getUniqueValue());
	grTile.orderBy('u_title');
	grTile.query();

	if(!grTile.hasNext()) return;

	var allUserCriteria = SNC.UserCriteriaLoader.getAllUserCriteria();
	while(grTile.next()){

		var canRead = checkUserCriteria(grTile.getValue('sys_id'), allUserCriteria);
		if(canRead == false) continue;

		var item = {};

		item.title = grTile.getValue('u_title');
		item.short_description = grTile.getValue('u_short_description');
		item.image = grTile.getDisplayValue('u_image');

		var grPage = GlideRecord('sp_page');
		grPage.get(grTile.getValue('u_page'));

		// base query string
		page = "?id=" + grPage.getValue('sys_name');
		title = "&title=" + grTile.getValue('u_title');
		image = "&image=" + grTile.getValue('u_image');
		item.url = page + title + image;

		if(grTile.getValue("u_type") == "u_sc_category"){
			item.url += "&sc_category=" + grTile.getValue("u_sc_category") + "&sys_id=" + grTile.getValue("u_sc_category");
		}
		else if(grTile.getValue("u_type") == "u_kb_category"){
			item.url += "&kb_category=" + grTile.getValue("u_kb_category");
		}
		else if(grTile.getValue("u_type") == "u_url"){
			item.url = grTile.getValue("u_url");
		}

		data.items.push(item);
	}
})();

function checkUserCriteria(quickLink, allUserCriteria){
	var grCanRead = new GlideRecord('u_quick_links_can_read');
	grCanRead.addQuery('u_quick_link', quickLink);
	grCanRead.query();

	var canRead = null;
	if(grCanRead.hasNext()){
		canRead = false;
		while(grCanRead.next()) {
			if(allUserCriteria.indexOf(grCanRead.u_can_read.getValue('sys_id')) > -1){
				canRead = true;
				break;
			}
		}
	}
	else{
		// no criteria was defined so by default everyone has access to this tile
		canRead = true;
	}
	return canRead;
}
